    trigger_conditions: {
      minimum_engagement_score: 6,
      days_in_sequence: 7,
      email_open_threshold: 40
    }
  }
};

return [
  {
    json: {
      config: masterConfig,
      timestamp: new Date().toISOString(),
      workflow_status: "initialized"
    }
  }
];

// Process and filter Islamic education related content
const items = $input.all();
const processedLeads = [];
const islamicKeywords = [
  'quran', 'tajweed', 'arabic', 'islam', 'muslim', 'hadith',
  'recitation', 'prayer', 'dua', 'islamic studies', 'learn arabic',
  'new muslim', 'convert', 'revert', 'mosque', 'imam'
];

// Process IslamQA data
if (items[0]?.json?.questions) {
  items[0].json.questions.forEach(question => {
    const text = (question.title + ' ' + question.content).toLowerCase();
    const relevanceScore = islamicKeywords.filter(keyword => 
      text.includes(keyword)
    ).length;

    if (relevanceScore >= 2) {
      processedLeads.push({
        source: 'islamqa',
        type: 'question_asker',
        content: question.title,
        url: `https://islamqa.info/en/answers/${question.id}`,
        relevance_score: relevanceScore,
        learning_intent: text.includes('learn') || text.includes('how to'),
        timestamp: new Date().toISOString(),
        user_id: question.user_id || 'anonymous',
        categories: question.categories || []
      });
    }
  });
}

// Process Reddit data
if (items[1]?.json?.data?.children) {
  items[1].json.data.children.forEach(post => {
    const postData = post.data;
    const text = (postData.title + ' ' + postData.selftext).toLowerCase();
    const relevanceScore = islamicKeywords.filter(keyword => 
      text.includes(keyword)
    ).length;

    if (relevanceScore >= 2) {
      processedLeads.push({
        source: 'reddit',
        type: 'community_member',
        content: postData.title,
        url: `https://reddit.com${postData.permalink}`,
        relevance_score: relevanceScore,
        learning_intent: text.includes('learn') || text.includes('help') || text.includes('advice'),
        timestamp: new Date(postData.created_utc * 1000).toISOString(),
        user_id: postData.author,
        upvotes: postData.ups,
        comments: postData.num_comments
      });
    }
  });
}

// Process YouTube data
if (items[2]?.json?.items) {
  items[2].json.items.forEach(video => {
    const text = (video.snippet.title + ' ' + video.snippet.description).toLowerCase();
    const relevanceScore = islamicKeywords.filter(keyword => 
      text.includes(keyword)
    ).length;

    if (relevanceScore >= 3) {
      processedLeads.push({
        source: 'youtube',
        type: 'video_creator',
        content: video.snippet.title,
        url: `https://youtube.com/watch?v=${video.id.videoId}`,
        relevance_score: relevanceScore,
        learning_intent: text.includes('learn') || text.includes('tutorial') || text.includes('lesson'),
        timestamp: video.snippet.publishedAt,
        channel_id: video.snippet.channelId,
        channel_title: video.snippet.channelTitle
      });
    }
  });
}

// Score and prioritize leads
processedLeads.forEach(lead => {
  lead.priority_score = lead.relevance_score + 
    (lead.learning_intent ? 3 : 0) + 
    (lead.source === 'islamqa' ? 2 : 0) +
    (lead.source === 'reddit' && lead.upvotes > 10 ? 1 : 0);
});

// Sort by priority
processedLeads.sort((a, b) => b.priority_score - a.priority_score);

return processedLeads.map(lead => ({ json: lead }));

