try {
  // Parse Gemini response
  const geminiResponse = JSON.parse(leadData.candidates[0].content.parts[0].text);

  // Combine original data with AI analysis
  const enrichedLead = {
    // Original lead data
    ...originalLead,

    // AI analysis
