      originalLead.priority_score + 
      geminiResponse.conversion_probability + 
      geminiResponse.learning_urgency
    ) / 3,

    // Contact information (to be enriched)
    contact: {
      email: null,
      phone: null,
      social_profiles: {},
      verified: false
    },

    // Workflow status
    workflow_status: {
      stage: 'lead_qualified',
      sequence_assigned: null,
      last_contact: null,
      next_action: geminiResponse.recommended_approach,
      vapi_eligible: geminiResponse.conversion_probability >= 6
    },

    // Islamic context
    islamic_context: {
      likely_muslim: originalLead.relevance_score >= 4,
      new_muslim_potential: originalLead.content.toLowerCase().includes('convert') || 
                            originalLead.content.toLowerCase().includes('revert') ||
