// Process VAPI call results
const vapiResult = $input.first().json;

// Extract call outcome data
const callData = {
  call_id: vapiResult.id,
  customer_phone: vapiResult.customer.number,
  call_status: vapiResult.status,
  call_duration: vapiResult.endedReason === 'customer-ended-call' ? vapiResult.duration : 0,
  transcript: vapiResult.transcript || '',
  outcome: 'unknown',
  interest_level: 0,
  next_action: 'continue_email_sequence',
  notes: ''
};

// Analyze call outcome using keywords
const transcript = (vapiResult.transcript || '').toLowerCase();

if (transcript.includes('interested') || transcript.includes('yes') || transcript.includes('enroll')) {
  callData.outcome = 'interested';
  callData.interest_level = 8;
  callData.next_action = 'schedule_demo';
} else if (transcript.includes('not interested') || transcript.includes('no') || transcript.includes('not now')) {
  callData.outcome = 'not_interested';
  callData.interest_level = 2;
  callData.next_action = 'long_term_nurture';
} else if (transcript.includes('think about') || transcript.includes('consider') || transcript.includes('maybe')) {
  callData.outcome = 'considering';
  callData.interest_level = 6;
  callData.next_action = 'follow_up_in_week';
} else if (vapiResult.endedReason === 'voicemail') {
  callData.outcome = 'voicemail';
  callData.interest_level = 4;
  callData.next_action = 'email_follow_up';
}

// Add Islamic context analysis
if (transcript.includes('inshallah') || transcript.includes('allah') || transcript.includes('dua')) {
  callData.islamic_engagement = 'high';
} else if (transcript.includes('prayer') || transcript.includes('mosque') || transcript.includes('quran')) {
  callData.islamic_engagement = 'medium';
} else {
  callData.islamic_engagement = 'unknown';
}

return [{
  json: {
    ...callData,
    processed_at: new Date().toISOString(),
    workflow_stage: 'vapi_processed'
  }
}];

