// VAPI Control System
const controlData = $input.first().json;

// Update environment variable for VAPI status
const vapiControl = {
  action: controlData.action || 'status', // 'enable', 'disable', 'status'
  course_specific: controlData.course_specific || {},
  time_restrictions: controlData.time_restrictions || {},
  trigger_conditions: controlData.trigger_conditions || {},
  current_status: process.env.VAPI_ENABLED === 'true',
  timestamp: new Date().toISOString()
};

// Process the control action
switch (vapiControl.action) {
  case 'enable':
    vapiControl.new_status = true;
    vapiControl.message = 'VAPI calling system ENABLED';
    break;
  case 'disable':
    vapiControl.new_status = false;
    vapiControl.message = 'VAPI calling system DISABLED';
    break;
  case 'toggle':
    vapiControl.new_status = !vapiControl.current_status;
    vapiControl.message = `VAPI calling system ${vapiControl.new_status ? 'ENABLED' : 'DISABLED'}`;
    break;
  case 'course_toggle':
    vapiControl.course_toggles = controlData.course_toggles;
    vapiControl.message = 'Course-specific VAPI settings updated';
    break;
  default:
    vapiControl.message = `VAPI status: ${vapiControl.current_status ? 'ENABLED' : 'DISABLED'}`;
}

// Log the control action
vapiControl.log_entry = {
  timestamp: vapiControl.timestamp,
  action: vapiControl.action,
  previous_status: vapiControl.current_status,
  new_status: vapiControl.new_status,
  triggered_by: controlData.user || 'system',
  reason: controlData.reason || 'manual_control'
};

return [{ json: vapiControl }];

